/*
 * @lc app=leetcode id=30 lang=cpp
 *
 * [30] Substring with Concatenation of All Words
 */

#include "leetcode.h"

// @lc code=start
class Solution {
public:
    /*
    aaaaa
    0 1 2 3
    0 1 2 3
    */
    vector<int> findSubstring(string s, vector<string>& words) {
        if (s.size() == 0 || words.size() == 0 || s.size() < words[0].size()) return {};

        // word->count
        unordered_map<string, int> counter;
        // pos->word, because word length is fixed, so one pos must be mapped to one word
        unordered_map<int, string> pos_word;
        // all postion array, later sort it
        vector<int> pos_array;
        for (int i = 0; i < words.size(); i++) {
            counter[words[i]]++;
            if (counter[words[i]] > 1) continue;

            int p = 0;
            while (1) {
                auto found = s.find(words[i], p);
                if (found == s.npos) {
                    break;
                }
                pos_word[found] = words[i];
                pos_array.emplace_back(found);
                p = found + 1;
            }
        }
        if (pos_array.size() == 0) return {};
        sort(pos_array.begin(), pos_array.end());

        vector<int> ret;
        unordered_map<string, int> tmp = counter;
        int word_count = words.size();
        int word_len = words[0].size();
        int left = 0;
        int right = left;
        int cur_pos = pos_array[right];
        // calulate current postion, and according to the map: pos->word, we can count
        while (right < pos_array.size()) {
            string word = pos_word[cur_pos];
            counter[word]--;
            if (counter[word] >= 0) {
                if (--word_count == 0) ret.push_back(pos_array[left]);
            }
            if (counter[word] < 0 || word_count == 0) {
                left++;
                right = left;
                if (right >= pos_array.size()) break;
                cur_pos = pos_array[right];
                // reset counter and word_count
                counter = tmp;
                word_count = words.size();
                continue;
            }

            // calulate current postion
            cur_pos += word_len;
        }

        return ret;
    }
};
// @lc code=end

